import React, { useState, useEffect } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { useHistory } from "react-router";

function ProfilePage() {

    const history = useHistory();


    const [load, setLoad] = useState(true);
    //const[dataf,Dataf] = useState({});
    const [subscription, plan] = useState([]);
    const user_id = localStorage.getItem("id");
    console.log(user_id);

    // useEffect(() => {
    //   axios
    //     .get(`https://admin.lowdly.com/files-listings?userid=${user_id}`)
    //     .then((res) => {
    //       console.log(res.data);
    //       setProducts(res.data);
    //     })
    //     .catch((err) => {
    //       console.log(err);
    //       return toast.error("Something Went Wrong.");
    //     });
    // }, []);

    useEffect(() => {
      axios
        .get(`https://admin.lowdly.com/files-listings?userid=${user_id}`)
        .then((res) => {
          console.log(res.data);
          setProducts(res.data);
        })
        .catch((err) => {
          console.log(err);
          return toast.error("Something Went Wrong.");
        });
    }, [load]);

    setInterval(() => {
      setLoad(!load);
    }, 1 * 60 * 1000);

    //  useEffect(() => {

    //     axios.get(`https://admin.lowdly.com/files-listings?userid=${user_id}`)
    //       .then(res => {
    //         console.log(res.data) ;
    //         setProducts(res.data);
    //       })
    //       .catch(err => {
    //         console.log(err);
    //         return toast.error('Something Went Wrong.')
    //       })
    //   }, [])
    /*User uploaded Files listing end*/

    /* Get user detail by id */
    useEffect(() => {
      axios
        .get(`https://admin.lowdly.com/users/${user_id}`)
        .then((res) => {
          console.log(res.data);
          setUserdata(res.data);
          plan(res.data.subscription_plan);
        })
        .catch((err) => {
          console.log(err);
          //return toast.error('Something Went Wrong.')
        });
    }, []);
    /*End Get user detail by id*/
    products.forEach((element) => {
      //console.log(element);
      console.log(element.filename);
      const fname = element.filename;
      const fid = element.id;
      console.log(
        "https://lowdly-file-new.s3.us-east-2.amazonaws.com/audio_uploaded/" +
          element.filename +
          "-lowdly.mp3"
      );
      request(
        "https://lowdly-file-new.s3.us-east-2.amazonaws.com/audio_uploaded/" +
          element.filename +
          "-lowdly.mp3",
        function (error, response, body) {
          if (error) console.error("error:", error); // Print the error if one occurred
          console.log("statusCode:", response && response.statusCode); // Print the response status code if a response was received
          if (response.statusCode == 200) {
            const dataf = {
              file_url:
                "https://lowdly-file-new.s3.us-east-2.amazonaws.com/audio_uploaded/" +
                element.filename +
                "-lowdly.mp3",

              status: "2",
            };

            (async () => {
              const resf = await axios.put(
                `https://admin.lowdly.com/files-listings/${fid}`,
                dataf
              );
              console.log("done");
            })();
          }
        }
      );
    });
    /* Update user profile */
    const handleSubmit = async (e) => {
      e.preventDefault();
      var isError = false;
      setIsLoading(true);
      const data = new FormData();
      //const [selectedFile, setSelectedFile] = useState()
      if (selectedFile) {
        data.append("files", selectedFile);
        const res = await axios.post("https://admin.lowdly.com/upload", data);
        console.log(res.data);
        formData.profile_image = res.data[0].url;
      }
      // Finally Make API Call
      if (formData.password !== formData.cpassword) {
        isError = true;
        toast.error("password are not matching");
      }
      try {
        if (isError) return history.push("/profile");
        const response = await axios.put(
          `https://admin.lowdly.com/users/${user_id}`,
          formData
        );
        console.log(response.data);
        // if(response.status === 200)
        // console.log(response.data)
        setIsLoading(false);
        toast.success("Profile Updated Successfully", { autoClose: false });
        // history.push('/profile');
        window.location = "/profile";
      } catch (error) {
        setIsLoading(false);
        toast.error("Please try again: Something went wrong");
      }
    };
    /* Update user profile end*/

    return (
      <>
        <ExamplesNavbar />
        <ProfilePageHeader />
        <div className="section profile-content">
          <Container>
            <div className="owner">
              <div className="avatar">
                {user.profile_image ? (
                  <img
                    alt="..."
                    className="img-circle img-no-padding img-responsive"
                    src={`https://admin.lowdly.com${user.profile_image}`}
                  ></img>
                ) : (
                  <img
                    alt="..."
                    className="img-circle img-no-padding img-responsive"
                    src={require("assets/img/avtarface.jpg").default}
                  />
                )}
              </div>
              <div className="name">
                <h4 className="title">
                  {localStorage.getItem("username")} <br />
                </h4>
              </div>
            </div>
            <Row>
              <Col className="ml-auto mr-auto text-center" md="6">
                <br />
              </Col>
            </Row>
            <br />
            <div className="nav-tabs-navigation">
              <div className="nav-tabs-wrapper">
                <Nav role="tablist" tabs>
                  <NavItem>
                    <NavLink
                      className={activeTab === "1" ? "active" : ""}
                      onClick={() => {
                        toggle("1");
                      }}
                    >
                      <strong> Files</strong>
                    </NavLink>
                  </NavItem>
                  <NavItem>
                    <NavLink
                      className={activeTab === "2" ? "active" : ""}
                      onClick={() => {
                        toggle("2");
                      }}
                    >
                      <strong>Subscription Plan</strong>
                    </NavLink>
                  </NavItem>
                  <NavItem>
                    <NavLink
                      className={activeTab === "3" ? "active" : ""}
                      onClick={() => {
                        toggle("3");
                      }}
                    >
                      <strong> Settings</strong>
                    </NavLink>
                  </NavItem>
                </Nav>
              </div>
            </div>
            {/* Tab panes */}
            <TabContent className="following" activeTab={activeTab}>
              <TabPane tabId="1" id="follows">
                <Row>
                  <div className="table-responsive">
                    <table className="table table-striped">
                      <thead>
                        <tr>
                          <th>File name</th>
                          <th>Details</th>
                          <th>Status</th>
                        </tr>
                      </thead>
                      <tbody>
                        {products.length === 0 ? (
                          <tr>
                            <td colSpan="4">No Records Found </td>
                          </tr>
                        ) : (
                          <>
                            {products
                              .filter(
                                (product) =>
                                  Date.now() - 604800000 <
                                  Date.parse(product.updated_at)
                              )
                              .map((product) => (
                                <tr>
                                  <td>{product.filename}</td>
                                  {product.status == "0" ? (
                                    <td>
                                      <span class="badge badge-primary">
                                        Estimated completion by{" "}
                                        {estimation(
                                          Date.parse(product.created_at)
                                        ).toLocaleString()}{" "}
                                      </span>
                                    </td>
                                  ) : product.status == "1" ? (
                                    <td>
                                      <span class="badge badge-warning">
                                        In Queue....
                                      </span>
                                    </td>
                                  ) : (
                                    <td>
                                      <a
                                        href={product.file_url}
                                        download={product.file_url}
                                        target="_blank"
                                      >
                                        <i className="nc-icon nc-cloud-download-93" />
                                        Download
                                      </a>
                                    </td>
                                  )}

                                  {product.status == "0" ? (
                                    <td>Processing... </td>
                                  ) : product.status == "1" ? (
                                    <td>Processing will begin shortly</td>
                                  ) : (
                                    <td>
                                      ExpiresAt{" "}
                                      {calculate(
                                        Date.parse(product.updated_at)
                                      ).toLocaleString()}
                                    </td>
                                  )}
                                </tr>
                              ))}
                          </>
                        )}
                      </tbody>
                    </table>
                  </div>
                </Row>
              </TabPane>

              <TabPane className="text-center" tabId="2" id="following">
                <Card>
                  <CardHeader>Subscription Plan</CardHeader>
                  <CardBody>
                    <CardTitle>
                      <strong>{subscription.name} </strong>
                    </CardTitle>
                    <CardText>{subscription.description}</CardText>
                    {subscription.id == "1" ? (
                      <Button href="/Upgrade" color="warning">
                        Upgrade
                      </Button>
                    ) : subscription.id == "2" ? (
                      <Button href="/Upgrade" color="warning">
                        Upgrade
                      </Button>
                    ) : (
                      <Button href="#" color="danger">
                        Cancel Subscription
                      </Button>
                    )}
                  </CardBody>
                </Card>
              </TabPane>

              <TabPane className="text-center" tabId="3" id="setting">
                <Form className="contact-form" onSubmit={handleSubmit}>
                  <Row>
                    <Col md="6">
                      <label></label>
                      <InputGroup>
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="nc-icon nc-single-02" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Name"
                          type="text"
                          defaultValue={user.name}
                          onChange={(e) =>
                            setFormData({ ...formData, name: e.target.value })
                          }
                        />
                      </InputGroup>
                    </Col>
                    <Col md="6">
                      <label></label>
                      <InputGroup>
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="nc-icon nc-email-85" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Email"
                          type="text"
                          defaultValue={user.email}
                          onChange={(e) =>
                            setFormData({ ...formData, email: e.target.value })
                          }
                          disabled
                        />
                      </InputGroup>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="6">
                      <label></label>
                      <InputGroup>
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="nc-icon nc-circle-10" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Username"
                          defaultValue={user.username}
                          type="text"
                          onChange={(e) =>
                            setFormData({ ...formData, username: e.target.value })
                          }
                          disabled
                        />
                      </InputGroup>
                    </Col>

                    <Col md="6">
                      <label></label>
                      <InputGroup>
                        <Input
                          placeholder="files"
                          id="files"
                          className="form-control"
                          name="profile_image"
                          type="file"
                          onChange={(event) =>
                            setSelectedFile(event.target.files[0])
                          }
                        />
                      </InputGroup>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="6">
                      <label></label>
                      <InputGroup>
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="nc-icon nc-lock-circle-open" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="New Password"
                          type="password"
                          onChange={(e) =>
                            setFormData({ ...formData, password: e.target.value })
                          }
                        />
                      </InputGroup>
                    </Col>
                    <Col md="6">
                      <label></label>
                      <InputGroup>
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="nc-icon nc-lock-circle-open" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Confirm Password"
                          type="password"
                          onChange={(e) =>
                            setFormData({
                              ...formData,
                              cpassword: e.target.value,
                            })
                          }
                        />
                      </InputGroup>
                    </Col>
                  </Row>
                  {isLoading && (
                    <div>
                      <img
                        alt="..."
                        width="100"
                        height="100"
                        src={require("assets/img/load.gif").default}
                      />
                      <strong>Please Wait...</strong>
                    </div>
                  )}
                  <Row>
                    <Col className="ml-auto mr-auto" md="4">
                      <Button className="btn-fill" color="danger" size="lg">
                        Update
                      </Button>
                    </Col>
                  </Row>
                </Form>
              </TabPane>
            </TabContent>
          </Container>
        </div>
        <DemoFooter />
      </>
    );
  }

  export default ProfilePage;
